mapper.select:
SELECT s.no no, s.title title, s.area1 area1, s.area2 area2, s.poom poom, s.role role, u.nick_name nickname, u.photo_url profilePic, s.regdate regdate FROM services s, users u WHERE u.no = s.user_no AND s.no IN (SELECT service_no FROM contracts WHERE (giver_no = #{userNo} or taker_no = #{userNo}) AND status = #{contractStatus} GROUP BY service_no)
AND s.user_no != #{userNo}
AND s.user_no = #{userNo}
