mapper.select:
select Count(*)
from coin_transactions
where 

	type = #{type}


	type < 21

and user_no = #{no}

select coin, contractNo, type, regDate, nickname from(select coin, contractNo, type, regDate, nickname, rownum r from(select ct.coin coin, ct.contract_no contractNo, ct.type, ct.regdate regDate, u.NICK_NAME nickname from COIN_TRANSACTIONS ct, users u WHERE ct.user_no = u.no AND u.no = #{userNo} AND
type = #{type}
type < 21
order by regDate DESC)) where r BETWEEN #{start} and #{end}

		 SELECT ct.coin coin, ct.contract_no contractNo, ct.type, ct.regdate, ct.user_no userNo
          FROM COIN_TRANSACTIONS ct, users u
          WHERE  ct.user_no = u.no
          AND u.no = #{userNo}
          AND #{startdate} < ct.regdate
          AND  ct.regdate < #{enddate}
          AND
      	 
			type = #{type}
		
		
			type < 21
		
         ORDER BY ct.regdate ASC
