mapper.select:
SELECT count(no)
FROM like_services
WHERE service_no = #{serviceNo}

	SELECT no, user_no userNo, service_no serviceNo
	FROM like_services
	WHERE user_no = #{userNo}
  	AND service_no = #{serviceNo}

mapper.delete:
DELETE 
FROM like_services
WHERE user_no = #{userNo}
AND service_no = #{serviceNo}
